<?php/** * The template for displaying search results pages * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#search-result * * @package WP_Bootstrap_4 */get_header(); ?><?php cat_breadcrumbs() ?>    <div class="container">        <div class="row">            <div class="col-12">                <header class="page-header mt-2r">                    <h3 class="archive__title title-head text-center text-uppercase">                        <span>                        <?php                        printf(esc_html__('Kết quả tìm kiếm: %s', 'wp-bootstrap-4'), '<span>' . get_search_query() . '</span>')                        ?>                        </span>                    </h3>                </header><!-- .page-header -->            </div>            <?php            if (have_posts()) : ?>                <?php while (have_posts()) : the_post(); ?>                    <article class="col-lg-6 col-md-12 col-12">                        <div class="v-post-item">                            <div class="card mb-5">                                <div class="row">                                    <div class="col-md-4 card-image pr-md-0">                                        <a href="<?php the_permalink(); ?>">                                            <?php                                            if (get_the_post_thumbnail() == '') {                                                ?>                                                <img class="v-img-icon" src="<?= the_field('v_service_icon', get_the_ID()) ?>" alt="">                                                <?php                                            } else {                                                the_post_thumbnail();                                            }                                            ?>                                        </a>                                    </div>                                    <div class="col-md-8 pl-md-0">                                        <div class="card-body">                                            <h3 class="card-title card-title-style">                                                <a href="<?php the_permalink(); ?>"><?php the_title() ?></a>                                            </h3>                                            <p class="card-text">                                                <small>Ngày đăng: <?php echo get_the_date() ?></small>                                            </p>                                            <p class="card-text card-text-style"><?php echo get_the_excerpt() ?></p>                                        </div>                                    </div>                                </div>                            </div>                        </div>                    </article>                <?php endwhile; ?>                <div class="col-12">                    <!-- Phân Trang -->                    <?php get_template_part('category-templates/pagination/pagination'); ?>                </div>            <?php else :                get_template_part('template-parts/content', 'none');            endif; ?>        </div>        <!-- /.row -->    </div>    <!-- /.container --><?phpget_footer();?>