<?php/** * The template for displaying search results pages * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#search-result * * @package WP_Bootstrap_4 */get_header(); ?><?phpget_template_part('template-parts/header-page', '', ['page_title' => 'Kết quả bạn cần']);?>    <div class="container my-5">        <div class="row">            <?php            if (have_posts()):                while (have_posts()):the_post();                    $post_id = get_the_ID();                    $post_image = get_the_post_thumbnail_url($post_id, 'full');                    ?>                    <div class="col-lg-3 col-md-4 col-6">                        <div class="v-service-item">                            <div class="v-service-img">                                <img src="<?php echo $post_image; ?>"                                     alt="<?php echo get_the_title(); ?>" loading="lazy">                                <div class="v-service-action">                                    <a class="v-btn-more" href="<?php the_permalink(); ?>"><i                                                class="fas fa-search-plus"></i>Xem chi tiết</a>                                </div>                            </div>                            <div class="v-service-info">                                <a href="<?php the_permalink(); ?>">                                    <h3><?php the_title(); ?></h3>                                </a>                                <p><?php echo get_the_excerpt(); ?></p>                            </div>                        </div> <!--/.v-service-item-->                    </div>                <?php endwhile;                wp_reset_query(); ?>            <?php endif; ?>        </div>        <!-- /.row -->    </div>    <!-- /.container --><?phpget_footer();?>